using FluentAssertions;

namespace AoC._2015.day01.v1.Tests;

public class FloorCalculatorTests
{
    [Fact]
    public void FromInstructionsReturnsZeroForBlankInput()
    {
        var finalFloor = FloorCalculator.GetFinalFloorFromInstructions("");

        finalFloor.Should().Be(0);
    }

    [Fact]
    public void FromInstructionsReturnsOneForOpeningBracket()
    {
        var finalFloor = FloorCalculator.GetFinalFloorFromInstructions("(");

        finalFloor.Should().Be(1);
    }

    [Fact]
    public void FromInstructionsReturnsNegativeOneForClosingBracket()
    {
        var finalFloor = FloorCalculator.GetFinalFloorFromInstructions(")");

        finalFloor.Should().Be(-1);
    }

    [InlineData("((", 2)]
    [InlineData("(())", 0)]
    [InlineData("()()", 0)]
    [InlineData("(((", 3)]
    [InlineData("(()(()(", 3)]
    [InlineData("))(((((", 3)]
    [Theory]
    public void FromInstructionsReturnsCorrectFloorFromRequirements(string instruction, int expectedFloor)
    {
        var finalFloor = FloorCalculator.GetFinalFloorFromInstructions(instruction);

        finalFloor.Should().Be(expectedFloor);
    }

